import { mapState, mapGetters, mapActions } from 'vuex'
import { AgGridVue } from "ag-grid-vue"
import "ag-grid-community/dist/styles/ag-grid.css";
import "ag-grid-community/dist/styles/ag-theme-blue.css";
import axios from 'axios'

const url = 'http://localhost:4081'

export default {
    name: 'Bookings',
    props: ['seller', 'handlePopup'],
    components: {
        AgGridVue
    },
    data() {
        return {
            columnDefs: [
                {   headerName: 'ID', field: 'id', cellClass: "grid-id", cellRenderer: (data) => {
                        return data.value.substring(0, 5).toUpperCase();  }},
                {   headerName: 'Product Name', field: 'productName' },
                {   headerName: 'Quantity', field: 'quantity' },
                {   headerName: 'Rate', field: 'rate' },
                {   headerName: 'Cost', field: 'cost' }
            ],
            rowData: null,
            rowSelection: null,
            overlayNoRowsTemplate: null
        }
    },
    beforeMount() {
        axios.get(url + "/bookings")
        .then(resp => {
            const products = this.$props.seller.products;
            const bookings = [];
            products.map(product => {
                const data = resp.data.filter(x => x.productId == product.id);
                data.map(booking => {
                    const item = {...booking, ...{ productName: product.name, rate: product.rate }};
                    bookings.push(item);
                });
            })
            this.rowData = bookings;
        })

        this.rowSelection = "single";
        this.overlayNoRowsTemplate = "No Active Bookings";
    },
    methods: {
        onGridReady(params) {
            this.gridApi = params.api;
            this.columnApi = params.columnApi;
        }
    }
}
